import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

function isEmailValid(email) {
  return /^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(email);
}

function Register({ onRegistered }) {
  const [jmeno, setJmeno] = useState('');
  const [email, setEmail] = useState('');
  const [heslo, setHeslo] = useState('');
  const [msg, setMsg] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async e => {
    e.preventDefault();
    if (jmeno.length < 3) {
      setMsg('Jméno musí mít alespoň 3 znaky');
      return;
    }
    if (!isEmailValid(email)) {
      setMsg('Zadejte platný email');
      return;
    }
    if (heslo.length < 5) {
      setMsg('Heslo musí mít alespoň 5 znaků');
      return;
    }
    const res = await fetch('http://localhost:3001/register', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ jmeno, email, heslo })
    });
    const data = await res.json();
    if (data.success) {
      setMsg('Registrace úspěšná! Můžete se přihlásit.');
      if (typeof onRegistered === "function") {
        onRegistered();
      }
    } else {
      setMsg(data.message || 'Chyba při registraci');
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <h2>Registrace</h2>
      <input
        placeholder="Jméno"
        value={jmeno}
        onChange={e => setJmeno(e.target.value)}
        required
      />
      <input
        type="email"
        placeholder="Email"
        value={email}
        onChange={e => setEmail(e.target.value)}
        required
      />
      <input
        type="password"
        placeholder="Heslo"
        value={heslo}
        onChange={e => setHeslo(e.target.value)}
        required
      />
      <button type="submit">Registrovat</button>
      <button
        type="button"
        style={{ marginLeft: '10px' }}
        onClick={() => navigate('/')}
      >
        Zpět na hlavní stranu
      </button>
      {msg && <div style={{ color: msg.includes('úspěšná') ? 'green' : 'red' }}>{msg}</div>}
    </form>
  );
}

export default Register;
