import React, { useEffect, useState } from 'react';
import Transfer from './Transfer';
import Transactions from './Transactions';

function Dashboard({ userId }) {
  const [transactions, setTransactions] = useState([]);
  const [account, setAccount] = useState(null);

  const getAuthHeaders = () => {
    const token = localStorage.getItem('token');
    return token ? { Authorization: `Bearer ${token}` } : {};
  };

  const loadAccount = async () => {
    try {
      const res = await fetch(`http://localhost:3001/account/${userId}`, {
        headers: { 'Content-Type': 'application/json', ...getAuthHeaders() }
      });
      if (!res.ok) throw new Error('Nepodařilo se načíst účet');
      const data = await res.json();
      setAccount(data);
    } catch (err) {
      console.error('loadAccount', err);
      setAccount(null);
    }
  };

  const loadTransactions = async () => {
    if (!account) return;
    const res = await fetch(`http://localhost:3001/transactions/${account.id}`, {
      headers: { 'Content-Type': 'application/json', ...getAuthHeaders() }
    });
    const data = await res.json();
    setTransactions(data || []);
  };

  useEffect(() => { loadAccount(); }, [userId]);
  useEffect(() => { loadTransactions(); }, [account]);

  const handleTransfer = async (targetId, amount) => {
    const res = await fetch('http://localhost:3001/transfer', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json', ...getAuthHeaders() },
      body: JSON.stringify({ od: account.id, do_: targetId, castka: Number(amount) })
    });
    const text = await res.text();
    let data;
    try { data = JSON.parse(text); } catch { data = { success: false, error: text || 'Neznámá chyba' }; }

    console.log('/transfer', res.status, data);
    if (!res.ok || !data.success) return { success: false, error: data.error || 'Chyba při převodu' };

    setTransactions(prev => [data.transaction, ...prev]);
    await loadAccount();
    return { success: true };
  };

  if (!account) return <div className="card">Načítání...</div>;

  return (
    <div className="card">
      <div className="logo">
        <img src="/bank.png" alt="Bankovní logo" style={{ width: '60px', borderRadius: '50%' }} />
      </div>
      <h1>Bankovní aplikace</h1>
      <div className="balance">Vítejte! Zůstatek: {account ? `${account.zustatek} Kč` : '—'}</div>

      <h3>Převod peněz</h3>
      <Transfer accountId={account?.id} onTransfer={handleTransfer} disabled={!account} />

      <h3 style={{ textAlign: 'center', marginTop: 18 }}>Historie transakcí</h3>
      <Transactions transactions={transactions} />
    </div>
  );
}

export default Dashboard;
