import React, { useEffect, useState } from 'react';
import Transfer from './Transfer';

function Dashboard({ userId }) {
  const [account, setAccount] = useState(null);

  useEffect(() => {
    fetch(`http://localhost:3001/account/${userId}`)
      .then(res => res.json())
      .then(setAccount);
  }, [userId]);

  return account ? (
    <div>
      <h2>Vítejte! Zůstatek: {account.zustatek} Kč</h2>
      <Transfer accountId={account.id} />
      {/* Historii transakcí přidáme v dalším kroku */}
    </div>
  ) : (
    <div>Načítání...</div>
  );
}

export default Dashboard;
