import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

function Transactions({ transactions = [] }) {
  const { accountId } = useParams();
  const [filter, setFilter] = useState('');
  const [sortDesc, setSortDesc] = useState(true);
  const [filteredTransactions, setFilteredTransactions] = useState(transactions);

  useEffect(() => {
    // Filtrování podle účtu při změně accountId v URL
    const trans = transactions.filter(t =>
      t.ucet_od === accountId || t.ucet_do === accountId
    );
    setFilteredTransactions(trans);
  }, [accountId, transactions]);

  // Filtrování podle částky nebo účtu
  const filtered = filteredTransactions.filter(t =>
    filter === '' ||
    t.castka.toString().includes(filter) ||
    t.ucet_od.toString().includes(filter) ||
    t.ucet_do.toString().includes(filter)
  );

  // Řazení podle data
  const sorted = [...filtered].sort((a, b) =>
    sortDesc
      ? new Date(b.datum) - new Date(a.datum)
      : new Date(a.datum) - new Date(b.datum)
  );

  // Export do CSV
  const exportCSV = () => {
    const rows = [
      ['Datum', 'Částka', 'Od', 'Do'],
      ...sorted.map(t => [
        new Date(t.datum).toLocaleString('cs-CZ'),
        t.castka,
        t.ucet_od,
        t.ucet_do
      ])
    ];
    const csv = rows.map(r => r.join(';')).join('\n');
    const blob = new Blob([csv], { type: 'text/csv' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = 'transakce.csv';
    a.click();
    URL.revokeObjectURL(url);
  };

  return (
    <div>
      <h3>Historie transakcí</h3>
      <input
        placeholder="Filtr (částka, účet)"
        value={filter}
        onChange={e => setFilter(e.target.value)}
        style={{ marginRight: 8 }}
      />
      <button onClick={() => setSortDesc(!sortDesc)}>
        Řadit {sortDesc ? 'od nejnovějších' : 'od nejstarších'}
      </button>
      <button onClick={exportCSV} style={{ marginLeft: 8 }}>
        Exportovat do CSV
      </button>
      <ul>
        {sorted.map(t => (
          <li key={t.id}>
            {new Date(t.datum).toLocaleString('cs-CZ')} - {t.castka} Kč (od: {t.ucet_od}, do: {t.ucet_do})
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Transactions;

app.get('/transactions/:accountId', (req, res) => {
  const trans = transactions.filter(t => t.ucet_od == req.params.accountId || t.ucet_do == req.params.accountId);
  res.json(trans);
});
