import React, { useEffect, useState } from 'react';
import Transfer from './Transfer';
import Transactions from './Transactions';

function Dashboard({ accountId }) {
  const [transactions, setTransactions] = useState([]);
  const [msg, setMsg] = useState('');

  // Načtení transakcí při načtení stránky nebo po převodu
  const loadTransactions = () => {
    fetch(`http://localhost:3001/transactions/${accountId}`)
      .then(res => res.json())
      .then(data => setTransactions(data));
  };

  useEffect(() => {
    loadTransactions();
  }, [accountId]);

  // Funkce pro převod peněz
  const handleTransfer = async (targetId, amount) => {
    setMsg('');
    const res = await fetch('http://localhost:3001/transfer', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ od: accountId, do_: targetId, castka: Number(amount) })
    });
    const data = await res.json();
    if (data.success) {
      setMsg('Převod proběhl úspěšně!');
      loadTransactions();
    } else {
      setMsg('Chyba při převodu!');
    }
  };

  return (
    <div className="container">
      <h1>Bankovní aplikace</h1>
      <div>Vítejte! Zůstatek: 4000 Kč</div>
      <h3>Převod peněz</h3>
      <Transfer accountId={accountId} onTransfer={handleTransfer} />
      {msg && <div style={{ color: msg.includes('úspěšně') ? 'green' : 'red' }}>{msg}</div>}
      <h3>Historie transakcí</h3>
      <Transactions transactions={transactions || []} />
    </div>
  );
}

export default Dashboard;
