import React, { useState } from 'react';

function Transfer({ accountId, onTransfer }) {
  const [targetId, setTargetId] = useState('');
  const [amount, setAmount] = useState('');
  const [msg, setMsg] = useState('');
  const [loading, setLoading] = useState(false);

  const handleSubmit = async e => {
    e.preventDefault();
    setMsg('');
    if (!onTransfer) {
      console.error('onTransfer prop chybí');
      return;
    }
    try {
      setLoading(true);
      console.log('volám onTransfer', { accountId, targetId, amount });
      const success = await onTransfer(targetId, amount); // musí vrátit true/false
      if (success) {
        setMsg('Převod proběhl úspěšně!');
        setTargetId('');
        setAmount('');
      } else {
        setMsg('Chyba při převodu!');
      }
    } catch (err) {
      console.error(err);
      setMsg('Chyba při odesílání převodu.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input placeholder="ID cílového účtu" value={targetId} onChange={e => setTargetId(e.target.value)} required />
      <input placeholder="Částka" type="number" value={amount} onChange={e => setAmount(e.target.value)} required />
      <button type="submit" disabled={loading}>{loading ? 'Odesílám…' : 'Odeslat'}</button>
      {msg && <div style={{ color: msg.includes('úspěšně') ? 'green' : 'red' }}>{msg}</div>}
    </form>
  );
}

export default Transfer;
