import React, { useState } from 'react';

function Transactions({ transactions = [] }) {
  const [filter, setFilter] = useState('');
  const [sortDesc, setSortDesc] = useState(true);

  const filtered = transactions.filter(t =>
    filter === '' ||
    t.castka.toString().includes(filter) ||
    t.ucet_od.toString().includes(filter) ||
    t.ucet_do.toString().includes(filter)
  );

  const sorted = [...filtered].sort((a, b) =>
    sortDesc ? new Date(b.datum) - new Date(a.datum) : new Date(a.datum) - new Date(b.datum)
  );

  if (!sorted.length) return <div style={{ color: '#666', marginTop: 12 }}>Zatím žádné transakce.</div>;

  return (
    <div>
      <input placeholder="Filtr (částka, účet)" value={filter} onChange={e => setFilter(e.target.value)} style={{ width: '100%', padding: '10px', marginBottom: 12 }} />
      <div className="transactions-controls" style={{ justifyContent: 'center', marginBottom: 12 }}>
        <button className="btn" onClick={() => setSortDesc(!sortDesc)}>Řadit {sortDesc ? 'od nejnovějších' : 'od nejstarších'}</button>
      </div>
      <ul style={{ marginTop: 8 }}>
        {sorted.map(t => (
          <li key={t.id}>{new Date(t.datum).toLocaleString('cs-CZ')} - {t.castka} Kč (od: {t.ucet_od}, do: {t.ucet_do})</li>
        ))}
      </ul>
    </div>
  );
}

export default Transactions;
