import React, { useEffect, useState } from 'react';
import Transfer from './Transfer';
import Transactions from './Transactions';

function Dashboard({ userId }) {
  const [transactions, setTransactions] = useState([]);
  const [msg, setMsg] = useState('');
  const [account, setAccount] = useState(null);

  const loadAccount = () => {
    fetch(`http://localhost:3001/account/${userId}`)
      .then(res => res.json())
      .then(data => setAccount(data));
  };

  const loadTransactions = async () => {
    try {
      if (!account) return;
      const res = await fetch(`http://localhost:3001/transactions/${account.id}`);
      if (!res.ok) throw new Error('Chyba při načítání transakcí');
      const data = await res.json();
      setTransactions(data);
    } catch (err) {
      console.error(err);
      // zobrazit uživatelsky přívětivou chybu (toast nebo stav)
      setMsg('Nepodařilo se načíst historii transakcí.');
    }
  };

  useEffect(() => {
    loadAccount();
  }, [userId]);

  useEffect(() => {
    loadTransactions();
  }, [account]);

  const handleTransfer = async (targetId, amount) => {
    const res = await fetch('http://localhost:3001/transfer', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ od: account.id, do_: targetId, castka: Number(amount) })
    });
    const data = await res.json();
    if (data.success) {
      loadAccount();
      loadTransactions();
      return true;
    }
    return false;
  };

  if (!account) return <div>Načítání...</div>;

  return (
    <div className="card">
      <div className="logo">
        <img src="/bank.png" alt="Bankovní logo" style={{ width: '60px', borderRadius: '50%' }} />
      </div>
      <h1>Bankovní aplikace</h1>
      <div style={{ fontSize: '1.2em', fontWeight: 'bold', margin: '15px 0', color: '#333' }}>
        Vítejte! Zůstatek: {account.zustatek} Kč
      </div>
      <h3>Převod peněz</h3>
      <Transfer accountId={account.id} onTransfer={handleTransfer} />
      {msg && <div style={{ color: msg.includes('úspěšně') ? 'green' : 'red' }}>{msg}</div>}
      <h3>Historie transakcí</h3>
      <Transactions transactions={transactions} />
    </div>
  );
}

export default Dashboard;
