import React, { useState } from 'react';

function Transactions({ transactions = [] }) {
  const [filter, setFilter] = useState('');
  const [sortDesc, setSortDesc] = useState(true);

  const filtered = transactions.filter(t =>
    filter === '' ||
    t.castka.toString().includes(filter) ||
    t.ucet_od.toString().includes(filter) ||
    t.ucet_do.toString().includes(filter)
  );

  const sorted = [...filtered].sort((a, b) =>
    sortDesc
      ? new Date(b.datum) - new Date(a.datum)
      : new Date(a.datum) - new Date(b.datum)
  );

  // exportCSV může zůstat pokud chcete export později; není ale vykresleno
  const exportCSV = () => {
    const rows = [
      ['Datum', 'Částka', 'Od', 'Do'],
      ...sorted.map(t => [
        new Date(t.datum).toLocaleString('cs-CZ'),
        t.castka,
        t.ucet_od,
        t.ucet_do
      ])
    ];
    const csv = rows.map(r => r.join(';')).join('\n');
    const blob = new Blob([csv], { type: 'text/csv' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = 'transakce.csv';
    a.click();
    URL.revokeObjectURL(url);
  };

  if (!transactions || transactions.length === 0) {
    return <div style={{ color: '#666', marginTop: 12 }}>Zatím žádné transakce.</div>;
  }
  return (
    <div>
      {/* odstraněn vnitřní nadpis */}
      <div style={{ marginBottom: 12 }}>
        <input
          placeholder="Filtr (částka, účet)"
          value={filter}
          onChange={e => setFilter(e.target.value)}
          style={{ marginRight: 8, padding: '10px', width: '100%', boxSizing: 'border-box' }}
        />
        <div className="transactions-controls" style={{ marginTop: 8 }}>
          {/* Zůstal pouze tento jeden button, bude centrován pomocí CSS */}
          <button className="btn" onClick={() => setSortDesc(!sortDesc)}>
            Řadit {sortDesc ? 'od nejnovějších' : 'od nejstarších'}
          </button>
        </div>
      </div>

      <ul>
        {sorted.map(t => (
          <li key={t.id}>
            {new Date(t.datum).toLocaleString('cs-CZ')} - {t.castka} Kč (od: {t.ucet_od}, do: {t.ucet_do})
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Transactions;
