import React, { useState } from 'react';

function Transfer({ accountId, onTransfer }) {
  const [targetId, setTargetId] = useState('');
  const [amount, setAmount] = useState('');
  const [msg, setMsg] = useState('');

  const handleSubmit = async e => {
    e.preventDefault();
    setMsg('');
    const res = await fetch('http://localhost:3001/transfer', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ od: accountId, do_: targetId, castka: Number(amount) })
    });
    const data = await res.json();
    if (data.success) {
      setMsg('Převod proběhl úspěšně!');
      setTargetId('');
      setAmount('');
      if (onTransfer) onTransfer(); // <-- This line was changed
    } else {
      setMsg('Chyba při převodu!');
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <h3>Převod peněz</h3>
      <input
        placeholder="ID cílového účtu"
        value={targetId}
        onChange={e => setTargetId(e.target.value)}
        required
      />
      <input
        placeholder="Částka"
        type="number"
        value={amount}
        onChange={e => setAmount(e.target.value)}
        required
      />
      <button type="submit">Odeslat</button>
      {msg && <div style={{ color: msg === 'Převod proběhl úspěšně!' ? 'green' : 'red' }}>{msg}</div>}
    </form>
  );
}

export default Transfer;
