import React, { useEffect, useState } from 'react';

function Transactions({ accountId }) {
  const [transactions, setTransactions] = useState([]);

  useEffect(() => {
    fetch(`http://localhost:3001/transactions/${accountId}`)
      .then(res => res.json())
      .then(setTransactions);
  }, [accountId]);

  return (
    <div>
      <h3>Historie transakcí</h3>
      <ul>
        {transactions.map(t => (
          <li key={t.id}>
            {t.datum}: {t.ucet_od === accountId ? '-' : '+'}{t.castka} Kč (od: {t.ucet_od}, do: {t.ucet_do})
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Transactions;
