import React, { useEffect, useState } from 'react';
import Transfer from './Transfer';
import Transactions from './Transactions';

function Dashboard({ userId }) {
  const [account, setAccount] = useState(null);

  useEffect(() => {
    const token = localStorage.getItem('token');
    fetch(`http://localhost:3001/account/${userId}`, {
      headers: { Authorization: `Bearer ${token}` }
    })
      .then(res => res.json())
      .then(setAccount);
  }, [userId]);

  return account ? (
    <div>
      <h2>Vítejte! Zůstatek: {account.zustatek} Kč</h2>
      <Transfer accountId={account.id} />
      <Transactions accountId={account.id} />
    </div>
  ) : (
    <div>Načítání...</div>
  );
}

export default Dashboard;
app.get('/transactions/:accountId', (req, res) => {
  const trans = transactions.filter(t => t.ucet_od == req.params.accountId || t.ucet_do == req.params.accountId);
  res.json(trans);
});
