import React, { useState } from 'react';

function Login({ onLogin, onShowRegister }) {
  const [email, setEmail] = useState('');
  const [heslo, setHeslo] = useState('');
  const [loading, setLoading] = useState(false);
  const [msg, setMsg] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMsg('');
    setLoading(true);
    try {
      const res = await fetch('http://localhost:3001/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email, heslo })
      });

      const data = await res.json();
      if (!res.ok || !data.success) {
        setMsg(data.error || 'Přihlášení selhalo');
      } else {
        // ulož token a zavolej callback
        localStorage.setItem('token', data.token);
        if (onLogin) onLogin(data.user);
        // případně přesměrování: window.location = '/';
      }
    } catch (err) {
      console.error('Login error', err);
      setMsg('Síťová chyba při přihlášení');
    } finally {
      setLoading(false);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input type="email" placeholder="Email" value={email} onChange={e => setEmail(e.target.value)} required />
      <input type="password" placeholder="Heslo" value={heslo} onChange={e => setHeslo(e.target.value)} required />
      <button type="submit" disabled={loading}>{loading ? 'Přihlašuji…' : 'Přihlásit se'}</button>
      <div style={{ marginTop: 8 }}>
        <button type="button" onClick={() => onShowRegister && onShowRegister()} style={{ background: 'transparent', border: 'none', color: '#234a63', cursor: 'pointer' }}>
          Nemáte účet? Zaregistrujte se
        </button>
      </div>
      {msg && <div style={{ color: 'red', marginTop: 8 }}>{msg}</div>}
    </form>
  );
}

export default Login;
