import React, { useEffect, useState } from 'react';

function Transactions({ accountId }) {
  const [transactions, setTransactions] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch(`http://localhost:3001/transactions/${accountId}`);
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        const data = await response.json();
        setTransactions(data);
      } catch (error) {
        console.error('There was a problem with the fetch operation:', error);
      }
    };

    fetchData();
  }, [accountId]);

  return (
    <div>
      <h3>Historie transakcí</h3>
      <ul>
        {transactions.length > 0 ? (
          transactions.map(t => (
            <li key={t.id}>
              {t.datum}: {t.ucet_od === accountId ? '-' : '+'}{t.castka} Kč (od: {t.ucet_od}, do: {t.ucet_do})
            </li>
          ))
        ) : (
          <li>Žádné transakce nenalezeny.</li>
        )}
      </ul>
    </div>
  );
}

export default Transactions;
