import React, { useState, useEffect } from 'react';
import Login from './components/Login';
import Register from './components/Register';
import Dashboard from './components/Dashboard';

function App() {
  const [user, setUser] = useState(() => {
    const u = localStorage.getItem('user');
    return u ? JSON.parse(u) : null;
  });

  useEffect(() => {
    if (!user) {
      const token = localStorage.getItem('token');
      if (!token) return;
      // volitelně: dekódovat token nebo zavolat /me endpoint; tady najdeme user z localStorage
      const u = localStorage.getItem('user');
      if (u) setUser(JSON.parse(u));
    }
  }, []);

  const handleLogin = (userObj) => {
    localStorage.setItem('user', JSON.stringify(userObj)); // uložíme user pro refresh
    setUser(userObj);
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    setUser(null);
  };

  return (
    <div className="wrapper">
      {!user ? (
        <div className="card">
          <div className="logo">
            <img src="/bank.png" alt="Bankovní logo" style={{ width: '60px', borderRadius: '50%' }} />
          </div>
          <h1>Bankovní aplikace</h1>
          {showRegister ? (
            <Register onRegistered={() => setShowRegister(false)} />
          ) : (
            <>
              <Login onLogin={handleLogin} />
              <button style={{ marginTop: '12px' }} onClick={() => setShowRegister(true)}>
                Registrovat nový účet
              </button>
            </>
          )}
        </div>
      ) : (
        <Dashboard userId={user.id} onLogout={handleLogout} />
      )}
    </div>
  );
}

export default App;
