import React, { useState } from 'react';

function Transfer({ accountId }) {
  const [toId, setToId] = useState('');
  const [amount, setAmount] = useState('');
  const [msg, setMsg] = useState('');

  const handleTransfer = async e => {
    e.preventDefault();
    if (!toId || isNaN(toId) || Number(toId) <= 0) {
      setMsg('Zadejte platné ID cílového účtu');
      return;
    }
    if (!amount || isNaN(amount) || Number(amount) <= 0) {
      setMsg('Zadejte kladnou částku');
      return;
    }
    const res = await fetch('http://localhost:3001/transfer', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ od: accountId, do_: Number(toId), castka: Number(amount) })
    });
    const data = await res.json();
    setMsg(data.success ? 'Převod úspěšný' : 'Chyba při převodu');
  };

  return (
    <form onSubmit={handleTransfer}>
      <h3>Převod peněz</h3>
      <input
        placeholder="ID cílového účtu"
        value={toId}
        onChange={e => setToId(e.target.value)}
        required
      />
      <input
        placeholder="Částka"
        type="number"
        value={amount}
        onChange={e => setAmount(e.target.value)}
        required
      />
      <button type="submit">Odeslat</button>
      {msg && <div>{msg}</div>}
    </form>
  );
}

export default Transfer;
