import React, { useState } from 'react';

function Transfer({ accountId, onTransfer }) {
  const [targetId, setTargetId] = useState('');
  const [amount, setAmount] = useState('');
  const [msg, setMsg] = useState('');

  const handleSubmit = async e => {
    e.preventDefault();
    setMsg('');
    const success = await onTransfer(targetId, amount);
    if (success) {
      setMsg('Převod proběhl úspěšně!');
      setTargetId('');
      setAmount('');
    } else {
      setMsg('Chyba při převodu!');
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <h3>Převod peněz</h3>
      <input
        placeholder="ID cílového účtu"
        value={targetId}
        onChange={e => setTargetId(e.target.value)}
        required
      />
      <input
        placeholder="Částka"
        type="number"
        value={amount}
        onChange={e => setAmount(e.target.value)}
        required
      />
      <button type="submit">Odeslat</button>
      {msg && <div style={{ color: msg.includes('úspěšně') ? 'green' : 'red' }}>{msg}</div>}
    </form>
  );
}

export default Transfer;
